sys_script_include.script:
var ValidateFutureDate = Class.create();
// Extend the global.AbstractAjaxProcessor class
ValidateFutureDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the getEmail function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address for the requested record
    validateDate: function() {
        var date = this.getParameter('sysparm_date');
        var niutils = new NeedItUtils();
        var isInvalid = niutils.isDatePast(date) || niutils.isDareToday(date);

        return isInvalid;
    },
    type: 'ValidateFutureDate'
});
